import React from 'react';
import '../styles/Main.css';
import { Link } from "react-router-dom";

const Main = () => (
  <main>
    {/* Hero Section */}
    <section className="hero">
      <div className="hero-content">
        <h1>BREAKING CHAINS OF FARMER EXPLOITATION</h1>
        <p className="subtitle">India's First AI-Powered Farmer Empowerment Platform</p>
        <button className="action-btn">Join Revolution</button>
      </div>
      <img src="https://currentaffairs.adda247.com/wp-content/uploads/multisite/sites/5/2021/07/17080224/pic.jpg" alt="Indian Farmer" className="hero-image" />
    </section>

    {/* About Section */}
    <section className="about-section section">
      <h2 className="section-title">About KisaanVerse</h2>
      <p className="about-content">
        KisaanVerse, powered by Team The Hack Pack, is a revolutionary digital marketplace connecting farmers, markets, and consumers.  
        We empower farmers with fair pricing, AI-driven insights, and direct market accessâ€”eliminating middlemen and ensuring transparency.  
        From smart crop planning and disease detection to competitive bidding and verified transactions, we leverage technology to  
        create a more profitable and sustainable future for Indian agriculture.  
        <br /><br />ðŸŒ± <strong>Empowering Farmers. Revolutionizing Agriculture.</strong>
      </p>
    </section>

    {/* Marketplace Section */}
    <section className="section" id="market">
      <h2 className="section-title">Digital Marketplace</h2>
      <div className="marketplace-grid">
        <div className="market-card">
          <img src="data:image/jpeg;base64," alt="Farm Produce" />
          <div className="card-content">
            <h3>Direct Selling</h3>
            <Link to="/marketplace"><button className="action-btn">Sell Now</button></Link>
          </div>
        </div>
        <div className="market-card">
          <img src="https://images.indianexpress.com/2021/09/apmc.jpg?w=414" alt="Farm Equipment" />
          <div className="card-content">
            <h3>Auction</h3>
            <Link to="/bid"><button className="action-btn">Sell Now</button></Link>
          </div>
        </div>
      </div>
    </section>

    {/* Disease Detection Section */}
    <section className="action-section disease-section">
      <h2>Detect Plant Diseases</h2>
      <p>
        Use our AI-powered scanning tool to identify plant diseases early. Upload images of your crops, 
        and get instant insights along with recommended treatments to protect your yield.
      </p>
      <button className="action-btn">Scan Your Crops Now</button>
    </section>

    {/* Crop Planning Section */}
    <section className="action-section crop-section">
      <h2>Smart Crop Planning</h2>
      <p>
        Optimize your farm layout with AI-driven crop recommendations based on soil quality, 
        climate conditions, and market trends. Get insights on intercropping, water efficiency, 
        and best seasonal planting strategies.
      </p>
      <Link to="/chatbot"><button className="action-btn">Start Planning</button></Link>
    </section>

    {/* NGO Section */}
    <section className="section" id="products">
      <h2 className="section-title">Our Partner NGOs</h2>
      <div className="ngo-grid">
        <div className="ngo-card">
          <img src="https://swadesfoundation.org/wp-content/uploads/2018/12/mobile-logo.png" alt="NGO 1" />
          <h4>Swadesh Foundation</h4>
          <p>Agricultural Support</p>
        </div>
        <div className="ngo-card">
          <img src="https://wotr.org/wp-content/uploads/2021/08/wotr-logo-with-tagline-white-01-1.png" alt="NGO 2" />
          <h4>Wotr</h4>
          <p>Farmer Education</p>
        </div>
        <div className="ngo-card">
          <img src="data:image/jpeg;base64," alt="NGO 3" />
          <h4>Kisan Vikas Trust</h4>
          <p>Rural Development</p>
        </div>
        <div className="ngo-card">
          <img src="http://graminpragati.in/img/logo.png" alt="NGO 4" />
          <h4>Gramin Pragati</h4>
          <p>Women Farmers Support</p>
        </div>
      </div>
    </section>
  </main>
);

export default Main;